import React from 'react';
import { Form, Upload, Icon, Input } from 'antd';

const FormItem = Form.Item;

class NormalCreatePostForm extends React.Component {
    normFile = (e) => {
        console.log('Upload event:', e);
        if (Array.isArray(e)) {
            return e;
        }
        return e && e.fileList;
    }

    beforeUpload = () => false

    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: { span: 6 },
            wrapperCol: { span: 14 },
        };

        return (
            <Form>
                <FormItem {...formItemLayout} label="Message">
                    {getFieldDecorator('message', {
                        rules: [{
                            required: true,
                            message: 'Please input your message',
                        }],
                    })(
                        <Input placeholder="Please input your message" />
                    )}
                </FormItem>
                <FormItem
                    {...formItemLayout}
                    label="Image"
                >
                    <div className="dropbox">
                        {getFieldDecorator('image', {
                            valuePropName: 'fileList',
                            getValueFromEvent: this.normFile,
                            rules: [{
                                required: true,
                                message: 'Please input your message',
                            }],
                        })(
                            <Upload.Dragger name="files" beforeUplaod={this.beforeUpload()}>
                                <p className="ant-upload-drag-icon">
                                    <Icon type="inbox" />
                                </p>
                                <p className="ant-upload-text">Click or drag file to this area to upload</p>
                                <p className="ant-upload-hint">Support for a single or bulk upload.</p>
                            </Upload.Dragger>
                        )}
                    </div>
                </FormItem>
            </Form>
        );
    }
}

export const CreatePostForm = Form.create()(NormalCreatePostForm);
