import React from 'react';
import { Modal, Button, message } from 'antd';
import { CreatePostForm } from './CreatePostForm';
import {API_ROOT, AUTH_HEADER, POS_KEY, TOKEN_KEY} from '../constants';

export class CreatePostButton extends React.Component {
    state = {
        ModalText: 'Content of the modal',
        visible: false,
        confirmLoading: false,
    }

    showModal = () => {
        this.setState({
            visible: true,
        });
    }

    handleOk = () => {
        this.form.validateFields((err, values) => {
            if (!err) {
                console.log(values);
                const {lat,lon} = JSON.parse(localStorage.getItem(POS_KEY));
                const token = localStorage.getItem(TOKEN_KEY);
                const formData = new FormData();
                formData.set('lat',lat);
                formData.set('lon',lon);
                formData.set('message', values.message);
                formData.set('image', values.image[0].originFileObj);

                this.setState({
                    confirmLoading: true,
                });

                fetch(`${API_ROOT}/post`, {
                  method: 'POST',
                    headers: {
                      Authorization: `${AUTH_HEADER} ${token}`,
                    },
                    body: formData,
                }).then((response) => {
                    if (response.ok) {
                        this.props.loadNearbyPosts();

                        return;
                    }
                    throw new Error(response.statusText);
                }).then(message.success('Post created successfully'))
                    .catch((e) => {
                  console.log(e);
                  message.error('Failed to create the post.');
                });

                setTimeout(() => {
                    this.setState({
                        visible: false,
                        confirmLoading: false,
                    });
                }, 2000);
            }
        });
    }

    handleCancel = () => {
        console.log('Clicked cancel button');
        this.setState({
            visible: false,
        });
    }

    saveFormRef = (formInstance) => {
        this.form = formInstance;
    }

    render() {
        const { visible, confirmLoading } = this.state;
        return (
            <div>
                <Button type="primary" onClick={this.showModal}>
                    Create New Post
                </Button>
                <Modal title="Create New Post"
                       visible={visible}
                       onOk={this.handleOk}
                       okText="Create"
                       confirmLoading={confirmLoading}
                       onCancel={this.handleCancel}
                >
                    <CreatePostForm ref={this.saveFormRef}/>
                </Modal>
            </div>
        );
    }
}
